//https://leetcode.com/problems/longest-substring-without-repeating-characters/submissions/
//O(n^2) scorro due volte l'array in avanti e indietro, per ogni sottostringa individuata controllo che non contenga caratteri duplicati
class Solution1 implements Solution {
    public int lengthOfLongestSubstring(String s) {
        int max = 0;
        for (int j = 0; j < s.length(); j++) {
            for (int i = s.length(); i > j; i--) {
                String candidate = s.substring(j, i);
                if (onlyUnique(candidate))
                    if (candidate.length() > max) max = candidate.length();
            }
            if (max > (s.length() - j)) return max;
        }
        return max;
    }

    //verifica molto veloce usando l'or coi bit ma funziona solo per span piccoli
    boolean onlyUnique2(String candidate) {
        long checker = 0;
        for (int i = 0; i < candidate.length(); i++) {
            int bitAtIndex = candidate.charAt(i) - ' ';
            if ((checker & (1 << bitAtIndex)) > 0)
                return false;
            checker = checker | (1 << bitAtIndex);
        }
        return true;
    }

    boolean onlyUnique(String candidate) {
        boolean[] chars = new boolean[100];
        for (int i = 0; i < candidate.length(); i++) {
            int index = candidate.charAt(i) - ' ';
            if (chars[index] == true)
                return false;
            chars[index] = true;
        }
        return true;
    }
}

//O(n) scorro tutto l'array, ogni volta che trovo un carattere controllo l'ultima volta che l'ho trovato, questa distanza è una sottostringa di cui testo la lunghezza
class Solution2 implements Solution {
    public int lengthOfLongestSubstring(String s) {
        Map<Character, Integer> positions = new HashMap<>();

        int max = 0;
        int start = 0;

        for (int i = 0; i < s.length(); i++) {
            char currentChar = s.charAt(i);

            Integer firstOcc = positions.get(currentChar);
            if (firstOcc != null && firstOcc >= start) {
                start = firstOcc + 1;
            }
            positions.put(currentChar, i);

            if (max < i - start + 1) max = i - start + 1;
        }
        return max;
    }
}